import React, { useEffect } from "react";
import { useState } from "react";
// import quotes from './assets/quotes.json'
import './App.scss'

let urlQuote = "https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/"

const App = () => {
  const [quote, setQuote] = useState("Work is 10")
  const [author, setAuthor] = useState("Charles Swindoll");
  const [randomNumber, setRandomNumber] = useState(0)
  const [quoteArr, setQuotesArr] = useState(null);

  const fetchQuotes = async (url) => {
    const response = await fetch(url)
    const parsedJSON = await response.json()
    setQuote(parsedJSON.quotes)
    console.log(parsedJSON)
  }

  useEffect(() => {
    fetchQuotes(urlQuote)
  }, [urlQuote])

  const handleNewQuote = () => {
    let randomInteger = Math.floor(quoteArr.length * Math.random())
    setRandomNumber(randomInteger)
    setQuote(quoteArr[randomInteger].quote)
    setAuthor(quoteArr[randomInteger].author)
  };



  return (
    <div className="App">
      <div id='quote-box'>
        <p id="text">"{quote}"</p>
        <p id="author">- {author}</p>
        <button id='new-quote' onClick={()=>handleNewQuote()}>New Quote</button>
        <a
          href="https://twitter.com/intent/tweet"
          id="tweet-quote"
          target="_blank"
          rel="noopener noreferrer"
        >
          Tweet
        </a>

      </div>
    </div>
  );
}

export default App;
